import styles from "./ServiceCard.module.css";
import { FullServiceCardInfo } from "./components/FullServiceCardInfo/FullServiceCardInfo";

export function ServiceCard({ id, name, description, image }) {
    return (
        <>
            <div className={styles.card}>
                <h2 className={styles.title}>{name}</h2>
                <img className={styles.image} src={image} alt={name} />
                <p className={styles.description} dangerouslySetInnerHTML={{ __html: description.replace(/\n/g, '<br>') }} />
                <div className={styles.buttons}>
                    <button>Подробнее</button>
                    <button>Добавить в заявки</button>
                </div>
            </div>

            <Routes>
                <Route path="/services/${id}" element={<FullServiceCardInfo/>} />
            </Routes>
        </>
    );
}
