import styles from "./ServiceList.module.css";
import { ServiceCard } from './../ServiceCard/ServiceCard';
import CartIcon from "./CartIcon.png";

export function ServiceList({ navigate , services, handleSearchClick }) {
    const [cartItems, setCartItems] = useState([]); // Состояние для хранения товаров в корзине
    const [isCartVisible, setIsCartVisible] = useState(false);

    return (
        <div className={styles.container}>
            <h2 className={styles.title}>Услуги</h2>

            <form className={styles.search_field} onSubmit={handleSearchClick}>
              <input
                type="text"
                name="q"
                aria-label="Поиск"
                placeholder="Поиск по услугам..."
              />
              <button type="submit">Поиск</button>
            </form>
            
            {services.length > 0 ? (
                services.map(service => (
                    <ServiceCard
                        key={service.id}
                        id={service.id}
                        name={service.name}
                        description={service.description}
                        image={service.image}
                        navigate={navigate}
                    />
                ))
            ) : (
                <p className={styles.title}>Сервисы не найдены.</p>
            )}

            {isCartVisible && (<img src={CartIcon} alt="" className={styles.cart} title="Документы в корзине"></img>)} 
        </div>
    );
}
