import styles from "./ServiceList.module.css";
import { ServiceCard } from './../ServiceCard/ServiceCard';

export function ServiceList() {
    const [services, setServices] = useState([]); // Состояние для хранения списка сервисов

    useEffect(() => {
        // Асинхронный запрос к API для получения списка сервисов
        const fetchServices = async () => {
            try {
                const response = await fetch("https://your-api-url.com/services"); // Замените на ваш реальный URL
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setServices(data); // Обновляем состояние с полученными сервисами
            } catch (error) {
                console.error("Ошибка при получении данных: ", error);
            }
        };

        fetchServices();
    }, []); // Эффект с пустым массивом, чтобы запросить данные только при монтировании компонента

    return (
        <div className={styles.container}>
            <h2 className={styles.title}>Услуги</h2>
            <form className={styles.search_field} action="/search" method="get">
                <input type="text" name="q" aria-label="Поиск" />
                <button type="submit">Поиск</button>
            </form>

            {/* Перебираем все сервисы и для каждого рендерим компонент ServiceCard */}
            <div className={styles.serviceList}>
                {services.map(service => (
                    <ServiceCard 
                        key={service.id} 
                        name={service.name} 
                        description={service.description} 
                        image={service.image} 
                    />
                ))}
            </div>
        </div>
    );
}
