import { useNavigate } from 'react-router-dom'; // Импортируем useNavigate
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; 
import { Hook } from './Hook';  // Импортируем хук
import { MainPage } from './components/MainPage/MainPage';  // Пример компонента
import { ServiceList } from './components/ServiceList/ServiceList';  // Пример компонента
import Styles from './App.module.css';

function App() {
  const hook = Hook();  // Используем хук для получения данных и функций
  const navigate = useNavigate();  // Получаем функцию navigate из хука

  return (
    <Router>  {/* Оборачиваем приложение в Router */}
      <div className={Styles.curtain}>
        <div className={Styles.full_logo} onClick={() => { 
          navigate('/main');  // Изменяем маршрут на /main при клике на логотип
        }}>
          <span className={Styles.logo}>IS-COM</span>
          <span className={Styles.under_logo}>ваш надежный <br />провайдер!</span>
        </div>

        <div className={Styles.right_buttons_container}>
          <button 
            className={Styles.right_buttons} 
            onClick={() => { 
              navigate('/services');  // Изменяем маршрут на /services при клике на кнопку
            }}
          >
            Услуги
          </button>
          <button className={Styles.right_buttons} onClick={() => console.log('Заявки clicked')}>
            Заявки
          </button>
        </div>
      </div>

      <Routes>
        <Route path="/services" element={<ServiceList services={hook.services} />} />
        <Route path="/main" element={<MainPage />} />
      </Routes>
    </Router>  
  );
}

export default App;
