import { useState, useEffect, useCallback } from "react";
import { useNavigate, useLocation } from 'react-router-dom';

export function Hook() {
  const navigate = useNavigate();
  const location = useLocation(); // Для отслеживания изменений маршрута
  const [services, setServices] = useState([]); // Состояние для хранения списка сервисов
  const [searchQuery, setSearchQuery] = useState(""); // Состояние для поискового запроса

  // Функция для выполнения запроса с фильтрацией по имени, обернутая в useCallback
  const fetchServices = useCallback(async () => {
    let url = "http://127.0.0.1:8000/api/services/?";
    
    if (searchQuery) {
      url += `name=${searchQuery}&`; // Добавляем параметр поиска по имени
    }

    try {
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      setServices(data); // Обновляем список сервисов
    } catch (error) {
      console.error("Ошибка при получении данных: ", error);
    }
  }, [searchQuery]); // Перезапускать только при изменении searchQuery

  // Эффект для запроса данных из API, который выполняется каждый раз, когда изменяется фильтр
  useEffect(() => {
    fetchServices();
  }, [fetchServices]); // Теперь fetchServices в списке зависимостей

  // Сбросить поисковый запрос при переходе на другую страницу
  useEffect(() => {
    setSearchQuery(""); // Сбрасываем поисковый запрос при изменении маршрута
  }, [location]);

  // Обработчик изменения поискового запроса
  const handleSearchClick = (e) => {
    e.preventDefault(); // Предотвращаем стандартное поведение формы (перезагрузку страницы)
    const searchText = e.target.elements.q.value; // Получаем значение поля ввода (name="q")
    console.log(searchText); // Проверяем, что текст из поля получен
    setSearchQuery(searchText); // Обновляем поисковый запрос
  };

  return {
    navigate,
    location,
    services,
    searchQuery,
    handleSearchClick,
  };
}
