import { useState, useEffect } from "react";

export function Hook() {
  const [services, setServices] = useState([]); // Состояние для хранения списка сервисов
  const [isServiceListVisible, setServiceListVisible] = useState(false);
  const [isMainPageVisible, setMainPageVisible] = useState(false);

  // Эффект для запроса данных из API
  useEffect(() => {
    const fetchServices = async () => {
      try {
        const response = await fetch("http://127.0.0.1:8000/api/services/");
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const data = await response.json();
        setServices(data);
      } catch (error) {
        console.error("Ошибка при получении данных: ", error);
      }
    };

    fetchServices();
  }, []);

  // Обработчик для кнопки "Услуги"
  const offAllComps = () => {
    setServiceListVisible(false);
    setMainPageVisible(false);
  };

  const handleServiceListClick = () => {
    offAllComps();
    setServiceListVisible(true);
  };

  const handleMainPageClick = () => {
    offAllComps();
    setMainPageVisible(true);
  };

  return {
    services,
    isServiceListVisible,
    isMainPageVisible,
    handleServiceListClick,
    handleMainPageClick,
  };
}
