import { useState, useEffect, useCallback } from "react";
import { useNavigate, useLocation } from 'react-router-dom';

export function Hook() {
  const navigate = useNavigate();
  const location = useLocation();
  const [services, setServices] = useState([]);
  const [applications, setApplications] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [type, setType] = useState("services"); // Тип данных (services или applications)

  const fetchData = useCallback(async () => {
    let url = `http://127.0.0.1:8000/api/${type}/?`;

    if (searchQuery) {
      url += `name=${searchQuery}&`; // Добавляем поисковый запрос, если он есть
    }

    try {
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();

      if (type === "services") {
        setServices(data);
      } else if (type === "applications") {
        setApplications(data);
      }
    } catch (error) {
      console.error("Ошибка при получении данных:", error);
    }
  }, [searchQuery, type]); // Зависимости: searchQuery и type

  // Изменение типа данных при смене маршрута
  useEffect(() => {
    setSearchQuery(""); // Сбрасываем поисковый запрос при изменении маршрута

    if (location.pathname === "/services") {
      setType("services"); // Меняем тип на "services" при маршруте /services
    } else if (location.pathname === "/applications") {
      setType("applications"); // Меняем тип на "applications" при маршруте /applications
    }
  }, [location.pathname]); // Зависимость только от маршрута

  // Запрашиваем данные при изменении типа или поискового запроса
  useEffect(() => {
    fetchData(); // Запрашиваем данные при изменении типа или поискового запроса
  }, [type, searchQuery, fetchData]); // Зависимости: type и searchQuery

  const handleSearchClick = (e) => {
    e.preventDefault(); // Предотвращаем стандартное поведение формы (перезагрузку)
    const searchText = e.target.elements.q.value; // Получаем текст поискового запроса
    setSearchQuery(searchText); // Обновляем поисковый запрос
  };

  return {
    navigate,
    location,
    services,
    applications,
    searchQuery,
    handleSearchClick,
  };
}
