import { useState, useEffect } from "react";
import { useNavigate, useLocation } from 'react-router-dom';

export function Hook() {
  const navigate = useNavigate();
  const location = useLocation(); // Для отслеживания изменений маршрута
  const [services, setServices] = useState([]); // Состояние для хранения списка сервисов
  const [applications, setApplications] = useState([]); // Состояние для хранения списка заявок
  const [searchQuery, setSearchQuery] = useState(""); // Состояние для поискового запроса

  // Функция для выполнения запроса с фильтрацией по имени (по умолчанию для сервисов)
  const fetchData = async (type) => {
    let url = `http://127.0.0.1:8000/api/${type}/?`;

    if (searchQuery.trim() !== "") {
      // Если есть поисковый запрос, добавляем его в URL
      url += `name=${encodeURIComponent(searchQuery)}`
    }

    console.log(url); // Проверка URL в консоли

    try {
      const response = await fetch(url);
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();

      if (type === "services") {
        setServices(data);
      } else if (type === "applications") {
        setApplications(data);
      }
    } catch (error) {
      console.error("Ошибка при получении данных:", error);
    }
  };

  // Сбросить поисковый запрос при переходе на другую страницу
  useEffect(() => {
    setSearchQuery(""); // Сбрасываем поисковый запрос при изменении маршрута

    if (location.pathname === "/services") {
      fetchData("services"); // Загружаем сервисы, если маршрут /services
    } else if (location.pathname === "/applications") {
      fetchData("applications"); // Загружаем заявки, если маршрут /applications
    }
  }, [location.pathname]);

  useEffect(() => {
    if (searchQuery.trim() !== "") {
      fetchData("services"); // Запрос сервисов при изменении поискового запроса
    }
  }, [searchQuery]); // Следим за изменением поискового запроса

  // Обработчик изменения поискового запроса
  const handleSearchClick = (e) => {
    e.preventDefault(); // Предотвращаем стандартное поведение формы (перезагрузку страницы)
    const searchText = e.target.elements.q.value; // Получаем значение поля ввода (name="q")
    console.log(searchText);
    setSearchQuery(searchText); // Обновляем поисковый запрос
  };

  return {
    navigate,
    location,
    services,
    applications,
    searchQuery,
    handleSearchClick,
  };
}
