import { Hook } from "./Hook"; // Импортируем хук
import { FullServiceCardInfo } from "./components/FullServiceCardInfo/FullServiceCardInfo";
import { ServiceList } from "./components/ServiceList/ServiceList";
import { MainPage } from "./components/MainPage/MainPage";
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom'; // Импортируем нужные компоненты из react-router-dom

import Styles from "./App.module.css";

function App() {
  const hook = Hook();  // Используем хук для получения данных и функций

  return (
    <Router> {/* Оборачиваем все приложение в Router */}
      <div className={Styles.curtain}>
        <div className={Styles.full_logo} onClick={hook.handleMainPageClick}>
          <span className={Styles.logo}>IS-COM</span>
          <span className={Styles.under_logo}>ваш надежный <br />провайдер!</span>
        </div>

        <div className={Styles.right_buttons_container}>
          {/* Используем ссылки для навигации, используя react-router */}
          <button className={Styles.right_buttons} onClick={hook.handleServiceListClick}>
            Услуги
          </button>
          <button className={Styles.right_buttons} onClick={() => console.log('Заявки clicked')}>
            Заявки
          </button>
        </div>
      </div>

      <Switch> {/* Switch рендерит только первый подходящий маршрут */}
        {/* Маршрут для главной страницы */}
        <Route path="/main">
          {hook.isMainPageVisible && <MainPage />}
        </Route>

        {/* Маршрут для списка услуг */}
        <Route path="/services">
          {hook.isServiceListVisible && <ServiceList services={hook.services} />}
        </Route>

        {/* Вы можете добавить другие маршруты, например для FullServiceCardInfo */}
        {/* <Route path="/service/:id" component={FullServiceCardInfo} /> */}
      </Switch>
    </Router>
  );
}

export default App;
