import styles from "./ServiceList.module.css";
import { ServiceCard } from './../ServiceCard/ServiceCard';
import { Hook } from "./../../Hook";

export function ServiceList({ services, handleSearchChange, searchQuery }) {
    const hook = Hook();
    return (
        <div className={styles.container}>
            <h2 className={styles.title}>Услуги</h2>

            <form className={styles.search_field} onSubmit={(e) => e.preventDefault()}>
                <input
                    type="text"
                    name="q"
                    value={searchQuery}
                    onChange={hook.handleSearchChange} // Обработчик для поля поиска
                    aria-label="Поиск"
                    placeholder="Поиск по услугам..."
                />
                <button type="submit">Поиск</button>
            </form>

            {/* Отображаем отфильтрованные сервисы */}
            {services.length > 0 ? (
                services.map(service => (
                    <ServiceCard
                        key={service.id}
                        id={service.id}
                        name={service.name}
                        description={service.description}
                        image={service.image}
                    />
                ))
            ) : (
                <p>Сервисы не найдены.</p>
            )}
        </div>
    );
}
