import { useState } from 'react';
import styles from "./ServiceList.module.css";
import { ServiceCard } from './../ServiceCard/ServiceCard';

export function ServiceList({ navigate, services, handleSearchClick }) {
  const [cart, setCart] = useState([]); // Состояние для корзины

  // Функция для добавления услуги в корзину
  const addToCart = (id, name, description, image) => {
    const newItem = { id, name, description, image };
    setCart(prevCart => [...prevCart, newItem]); // Добавляем услугу в корзину
  };

  // Функция для подтверждения добавления услуг из корзины в заявки
  const confirmCart = () => {
    // Логика для обработки добавления в заявки
    console.log("Добавлено в заявки:", cart);

    // Очистить корзину после подтверждения
    setCart([]);
  };

  return (
    <div className={styles.container}>
      <h2 className={styles.title}>Услуги</h2>

      <form className={styles.search_field} onSubmit={handleSearchClick}>
        <input
          type="text"
          name="q"
          aria-label="Поиск"
          placeholder="Поиск по услугам..."
        />
        <button type="submit">Поиск</button>
      </form>

      {services.length > 0 ? (
        services.map(service => (
          <ServiceCard
            navigate={navigate}
            key={service.id}
            id={service.id}
            name={service.name}
            description={service.description}
            image={service.image}
            addToCart={addToCart} // Передаем функцию добавления в корзину
          />
        ))
      ) : (
        <p className={styles.title}>Сервисы не найдены.</p>
      )}

      <div className={styles.cart}>
        <h3>Корзина</h3>
        {cart.length > 0 ? (
          <div>
            <ul>
              {cart.map((item, index) => (
                <li key={index}>
                  {item.name} - {item.description}
                </li>
              ))}
            </ul>
            <button onClick={confirmCart}>Подтвердить добавление в заявки</button>
          </div>
        ) : (
          <p>Корзина пуста.</p>
        )}
      </div>
    </div>
  );
}
