import { useState } from "react"; // Импортируем useState
import { FullServiceCardInfo } from "./components/FullServiceCardInfo/FullServiceCardInfo";
import { ServiceList } from "./components/ServiceList/ServiceList";
import { MainPage } from "./components/MainPage/MainPage";

import Styles from "./App.module.css";

function App() {
  // Состояние для управления видимостью ServiceList
  const [isServiceListVisible, setIsServiceListVisible] = useState(false);

  // Обработчик для кнопки "Услуги"
  const handleServicesClick = () => {
    setIsServiceListVisible(!isServiceListVisible); // Переключаем видимость
  };

  return (
    <>
      <div className={Styles.curtain}>
        <div className={Styles.full_logo} onClick={() => console.log('Главная clicked')}>
          <span className={Styles.logo}>IS-COM</span>
          <span className={Styles.under_logo}>ваш надежный <br />провайдер!</span>
        </div>

        <div className={Styles.right_buttons_container}>
          <button className={Styles.right_buttons} onClick={handleServicesClick}>
            Услуги
          </button>
          <button className={Styles.right_buttons} onClick={() => console.log('Заявки clicked')}>
            Заявки
          </button>
        </div>
      </div>

      {/* Показать ServiceList только если isServiceListVisible = true */}
      {isServiceListVisible && <ServiceList />}

      {/* <FullServiceCardInfo/> */}
      <MainPage/>
    </>
  );
}

export default App;
